import { Subject } from 'rxjs';
@Injectable({
    providedIn: 'root'
})
export class TaskService{
    // CreateTask : EventEmitter<string> = new EventEmitter<string>()
    //CreateTask = new Subject<string>();
    CreateTask = new Subject<string>()
    OnCreateTask(value:string){
        this.CreateTask.next(value)
    }
}
